`use strict`;

const JsonStorage = require(`${__dirname}/../../index.js`);

// init storage
const store = new JsonStorage(`${__dirname}/storage`);

(async () => {
  const c1 = await store.exists(`cars`, `car001`); // => true
  const c2 = await store.exists(`cars`); // => true
  const c3 = await store.exists(`cars`, `vw`); // => false
  const c4 = await store.exists(`bicycles`); // => false
  const c5 = await store.exists(`bicycles`, `hercules`); // => false
  
  console.log(c1);
  console.log(c2);
  console.log(c3);
  console.log(c4);
  console.log(c5);
})();
