`use strict`;

const JsonStorage = require(`${__dirname}/../../index.js`);

// init storage
const store = new JsonStorage(`${__dirname}/storage`);

(async () => {
  // get json files
  console.log(await store.get(`users`, `user001`)); // -> {name: `Jens`}
  console.log(await store.get(`users`, `user002`)); // -> {name: `Julia`}

  // get all filenames of a directory
  console.log(await store.get(`users`)); // => [`user001`, `user002`]

  // get all dirnames
  console.log(await store.get()); // => [`users`]
})();
