`use strict`;

const JsonStorage = require(`${__dirname}/../../index.js`);

// init storage
const store = new JsonStorage(`${__dirname}/storage`);

(async () => {
  // get the whole storage
  const obj = await store.getAll();
  console.log(obj);

  // get a whole dir
  const dir1 = await store.getAll(`cars`);
  console.log(dir1);
})();