`use strict`;

const JsonStorage = require(`${__dirname}/../../index.js`);

// init storage
const store = new JsonStorage(`${__dirname}/storage`);

// get json files
console.log(store.getSync(`users`, `user001`)); // -> {name: `Jens`}
console.log(store.getSync(`users`, `user002`)); // -> {name: `Julia`}

// get all filenames of a directory
console.log(store.getSync(`users`)); // => [`user001`, `user002`]

// get all dirnames
console.log(store.getSync()); // => [`users`]