`use strict`;

const JsonStorage = require(`${__dirname}/../../index.js`);

// init storage
const store = new JsonStorage(`${__dirname}/storage`);

// write files
(async () => {
  // set dir
  await store.set(`cars`);

  // set files
  await store.set(`users`, `user001`, { name: `Thomas` });
  await store.set(`users`, `user002`, { name: `Tom` });
})();

