`use strict`;

const JsonStorage = require(`${__dirname}/../../index.js`);

// json
const json = {
  user001: {
    name: `Tom`
  }
};

// write json sync
JsonStorage.writeSync(`${__dirname}/writeSync.json`, json);

// read json sync
console.log(JsonStorage.readSync(`${__dirname}/writeSync.json`));

(async () => {

  // write json async
  await JsonStorage.write(`${__dirname}/writeAsync.json`, json);

  // read json async
  console.log(await  JsonStorage.read(`${__dirname}/writeAsync.json`));

})();
