`use strict`;

const fs = require(`fs-extra`);
const checkNames = require(`${__dirname}/../helper/checkNames.js`);
const checkDirname = require(`${__dirname}/../helper/checkDirname.js`);

function existsSync(_this, dir, file) {

  //if file is set, check file
  if (file) {
    // throw error if names not valid
    checkNames(dir, file);

    return fs.existsSync(`${_this.dir}/${dir}/${file}.json`);
  } else {
    //else checks the dir
    checkDirname(dir);

    return fs.existsSync(`${_this.dir}/${dir}`);
  }

}

module.exports = existsSync;