`use strict`;

const fs = require(`fs-extra`);
const checkNames = require(`${__dirname}/../helper/checkNames.js`);

async function get(_this, dir, file) {
  // throw error if names not valid
  checkNames(dir, file);
  return await fs.readJson(`${_this.dir}/${dir}/${file}.json`);
}

module.exports = get;
