`use strict`;

function getAllSync(_this) {
  const all = {};

  const dirs = _this.getSync();

  for (let i = 0; i < dirs.length; i++) {
    const dir = dirs[i];

    if (dir !== `.git`) {
      all[dir] = {};
      _this.forEachSync(dir, (json, file) => {
        all[dir][file] = json;
      });
    }
  }

  return all;
}

module.exports = getAllSync;
