
`use strict`;

const fs = require(`fs-extra`);
const checkDirname = require(`${__dirname}/../helper/checkDirname.js`);

function getFilesSync(_this, dir) {
  checkDirname(dir);

  let filesRaw = fs.readdirSync(`${_this.dir}/${dir}`);
  let files = [];
  filesRaw.forEach((f) => {
    files.push(f.substring(0, f.length - 5));
  });

  return files;
}

module.exports = getFilesSync;