`use strict`;

const fs = require(`fs-extra`);
const checkNames = require(`${__dirname}/../helper/checkNames.js`);

function getSync(_this, dir, file) {
  // throw error if names not valid
  checkNames(dir, file);

  return fs.readJsonSync(`${_this.dir}/${dir}/${file}.json`);
}

module.exports = getSync;