`use strict`;

const fs = require(`fs-extra`);
const checkNames = require(`${__dirname}/../helper/checkNames.js`);
const checkDirname = require(`${__dirname}/../helper/checkDirname.js`);

function removeSync(_this, dir, file) {

  //if file is set, remove file
  if (file) {
    // throw error if names not valid
    checkNames(dir, file);

    const path = `${_this.dir}/${dir}/${file}.json`;
    fs.removeSync(path);

    if(_this.options.git){
      _this.git.addSync(`"${path}"`);
      _this.git.commitSync(`"${path}"`, `removeSync: ${file}`);
    }

  } else {
    //else remove the dir
    checkDirname(dir);

    const path = `${_this.dir}/${dir}`;

    fs.removeSync(path);

    if(_this.options.git){
      _this.git.addSync(`"${path}"`);
      _this.git.commitSync(`"${path}"`, `removeSync: ${dir}`);
    }
  }

}

module.exports = removeSync;