`use strict`;

const fs = require(`fs-extra`);
const checkDirname = require(`${__dirname}/../helper/checkDirname.js`);
const checkFilename = require(`${__dirname}/../helper/checkFilename.js`);

function setSync(_this, dir, file, json) {
  checkDirname(dir);
  fs.ensureDirSync(`${_this.dir}/${dir}`);

  if(file){
    checkFilename(file);
    const path = `${_this.dir}/${dir}/${file}.json`;
    fs.writeJsonSync(path, json, _this.options);

    if(_this.options.git){
      _this.git.addSync(`"${path}"`);
      _this.git.commitSync(`"${path}"`, `setSync: ${file}`);
    }
  }
}

module.exports = setSync;