`use strict`;

const isNameValid = require(`${__dirname}/isNameValid.js`);

/**
 * Checks if the dirname is valid.
 * If the dirname is not valid, this function will throw an error.
 * @param {string} dir Dirname
 */
function checkDirname(dir) {
  if (!isNameValid(dir)) {
    throw new Error(`The dirname "` + dir + `" is not valid.`);
  }
}

module.exports = checkDirname;