`use strict`;

const isNameValid = require(`${__dirname}/isNameValid.js`);

/**
 * Checks if the filename is valid.
 * If the filename is not valid, this function will throw an error.
 * @param {string} file Filename
 */
function checkFilename(file) {
  if (!isNameValid(file)) {
    throw new Error(`The filename "` + file + `" is not valid.`);
  }
}

module.exports = checkFilename;