`use strict`;

const checkDirname = require(`${__dirname}/checkDirname.js`);
const checkFilename = require(`${__dirname}/checkFilename.js`);

/**
 * Checks if the filename and the dirname are valid.
 * If the filename or the dirname are not valid, this function will throw an error.
 * @param {string} dir Dirname
 * @param {string} file Filename
 */
function checkNames(dir, file) {
  checkDirname(dir);
  checkFilename(file);
  return true;
}

module.exports = checkNames;