`use strict`;

const invalidChars = [`\\`, `/`, `?`, `|`, `>`, `<`, `:`, `*`, `"`, `+`, `,`, `;`, `=`, `[`, `]`, `..`];

/**
 * Checks if a filename or dirname is valid.
 * @param {string} name Name to be checked
 * @returns {boolean} True: Name is valid. False: Name is not valid.
 */
function isNameValid(name) {

  let valid = true;
  for (let i = 0; i < invalidChars.length; i++) {
    if (name.includes(invalidChars[i])) {
      valid = false;
      break;
    }
  }
  return valid;
}

module.exports = isNameValid;